import { useEffect, useState, useCallback } from 'react'

const Overwolf = overwolf.games.events
const REGISTER_RETRY_TIMEOUT = 10000

interface GameEventData<GameInfo, GameEvent> {
  info?: GameInfo
  events?: GameEvent[]
}

export const useGameEventProvider = <GameInfo, GameEvent>({
  displayLog,
}: {
  displayLog?: boolean
}) => {
  const [info, setInfo] = useState<GameInfo>()
  const [event, setEvent] = useState<GameEvent[]>()
  const [requiredFeatures, setFeatures] = useState<string[]>([])

  function handleGameEvent({ info, events }: GameEventData<any, any>) {
    info && setInfo(info)
    events && setEvent(events)
  }

  {
  }
  const registerToGepCallback = useCallback(
    ({ success, ...rest }: { success: boolean }) => {
      if (success) {
        Overwolf.onInfoUpdates2.removeListener(handleGameEvent)
        Overwolf.onNewEvents.removeListener(handleGameEvent)
        Overwolf.onInfoUpdates2.addListener(handleGameEvent)
        Overwolf.onNewEvents.addListener(handleGameEvent)
      } else
        setTimeout(() => {
          Overwolf.setRequiredFeatures(requiredFeatures, registerToGepCallback)
        }, REGISTER_RETRY_TIMEOUT)

      displayLog &&
        console.info(
          '[🐺 overwolf-hooks][🧰 useGameEventProvider][🔧 registerToGepCallback]',
          JSON.stringify({ success, ...rest }, null, 2),
        )
    },
    [requiredFeatures],
  )

  const runGep = useCallback(() => {
    Overwolf.setRequiredFeatures(requiredFeatures, registerToGepCallback)
  }, [requiredFeatures, registerToGepCallback])
  const setGameFeatures = useCallback(setFeatures, [])

  useEffect(() => {
    requiredFeatures.length && runGep()
    return () => {
      Overwolf.onInfoUpdates2.removeListener(handleGameEvent)
      Overwolf.onNewEvents.removeListener(handleGameEvent)
    }
  }, [runGep, requiredFeatures])

  return [{ info, event }, setGameFeatures] as const
}
import { useEffect, useState } from 'react'

export interface UseRunningGamePayload {
  gameRunning: boolean
  id: number
  title: string
  gameChanged: boolean
  isInFocus: boolean
}

export const useRunningGame = ({ displayLog }: { displayLog?: boolean }) => {
  const [game, setGame] = useState<UseRunningGamePayload | null>(null)

  function onGameInfoUpdated(payload: overwolf.games.GameInfoUpdatedEvent) {
    const gameRunning: UseRunningGamePayload = {
      gameRunning: payload?.gameInfo?.isRunning ?? false,
      id: Math.round((payload?.gameInfo?.id || 0) / 10),
      title: payload?.gameInfo?.title || '',
      gameChanged: payload?.gameChanged || false,
      isInFocus: payload?.focusChanged || false,
    }
    setGame(gameRunning)

    displayLog &&
      console.info(
        '[🐺 overwolf-hooks][🧰 useRunningGame][🔧 onGameInfoUpdatedPayload]',
        JSON.stringify(gameRunning, null, 2),
      )
  }

  function onGetRunningGameInfo(
    payload: overwolf.games.GetRunningGameInfoResult,
  ): void {
    displayLog &&
      console.info(
        '[🐺 overwolf-hooks][🧰 useRunningGame][🔧 onGetRunningGameInfo]',
        JSON.stringify(payload, null, 2),
      )
    setGame((currentGame) => ({
      gameChanged: currentGame?.gameChanged || false,
      id: Math.round((payload?.id || 0) / 10),
      title: payload?.title || '',
      gameRunning: payload?.isRunning ?? false,
      isInFocus: payload?.isInFocus ?? false,
    }))
  }

  useEffect(() => {
    overwolf.games.getRunningGameInfo(onGetRunningGameInfo)

    overwolf.games.onGameInfoUpdated.removeListener(onGameInfoUpdated)
    overwolf.games.onGameInfoUpdated.addListener(onGameInfoUpdated)

    return () => {
      overwolf.games.onGameInfoUpdated.removeListener(onGameInfoUpdated)
    }
  }, [])

  return [game] as const
}
