interface OwWindow extends WindowInfo {
  id: string;
  minimize: () => void;
  maximize: () => void;
  restore: () => void;
  close: () => void;
}
export const useWindow = (
  name: string,
  { displayLog }: { displayLog?: boolean }
) => {
  const [owDeclaredWindow, setOwDeclaredWindow] = useState<WindowInfo>();
  const [owWindow, setOwWindow] = useState<OwWindow>();

  const obtainDeclaredWindow = useCallback(async () => {
    try {
      const delclaredWindow = await obtainWindow(name);
      displayLog &&
        console.info(
          "[🐺 overwolf-hooks][🧰 useWindow][🔧 obtainDeclaredWindow]",
          JSON.stringify(delclaredWindow, null, 2)
        );
      setOwDeclaredWindow(delclaredWindow);
    } catch (error) {
      console.log({ error });
    }
  }, [name]);

  useEffect(() => {
    if (owDeclaredWindow) {
      const { id, ...otherWindowProps } = owDeclaredWindow;

      const createLog = (logAction = "") =>
        displayLog &&
        console.info(
          `[🐺 overwolf-hooks][🧰 useWindow][🔧 ${logAction}]`,
          JSON.stringify(owDeclaredWindow, null, 2)
        );
      const windowActions = {
        id,
        minimize: async () => {
          await minimize(id);
          createLog("minimize");
        },
        maximize: async () => {
          await maximize(id);
          createLog("maximize");
        },
        restore: async () => {
          await restore(id);
          createLog("restore");
        },
        close: async () => {
          await close(id);
          createLog("close");
        },
      };
      setOwWindow({ ...windowActions, ...otherWindowProps });
    }
  }, [owDeclaredWindow, displayLog]);

  useEffect(() => {
    obtainDeclaredWindow();
  }, [name]);

  return [owWindow] as const;
};
import { useState, useCallback } from 'react'

interface MousePostion {
  x: number
  y: number
}

const SIGNIFICANT_MOUSE_MOVE_THRESHOLD = 1

export const useDrag = (
  currentWindowID: string | null,
  { displayLog }: { displayLog?: boolean },
) => {
  const [owWindow, setOwWindow] = useState<string | null>(currentWindowID)
  const [
    initialMousePosition,
    setMousePosition,
  ] = useState<MousePostion | null>(null)
  const [isMouseDown, setMouseDown] = useState<boolean>(false)

  function onDragStart({
    clientX,
    clientY,
  }: React.MouseEvent<HTMLElement, MouseEvent>) {
    setMouseDown(true)
    setMousePosition({
      x: clientX,
      y: clientY,
    })
  }

  function isSignificantMouseMove({
    clientX,
    clientY,
  }: React.MouseEvent<HTMLElement, MouseEvent>) {
    if (!initialMousePosition) return false

    const diffX = Math.abs(clientX - initialMousePosition.x)
    const diffY = Math.abs(clientY - initialMousePosition.y)
    const isSignificant =
      diffX > SIGNIFICANT_MOUSE_MOVE_THRESHOLD ||
      diffY > SIGNIFICANT_MOUSE_MOVE_THRESHOLD

    return isSignificant
  }

  function onMouseMove(event: React.MouseEvent<HTMLElement, MouseEvent>) {
    if (!isMouseDown || !isSignificantMouseMove(event)) return
    setMouseDown(false)
    if (owWindow) {
      overwolf.windows.dragMove(owWindow)
      displayLog &&
        console.info(
          '[🐺 overwolf-hooks][🧰 useDrag][🔧 onGameInfoUpdatedPayload] ',
          JSON.stringify(
            { clientX: event.clientX, clientY: event.clientY },
            null,
            2,
          ),
        )
    }
  }

  const setCurrentWindowID = useCallback((id: string) => {
    setOwWindow(id)
  }, [])

  return { setCurrentWindowID, onDragStart, onMouseMove } as const
}
